SmartSqlMap.Update:
update ACT_RU_JOB set TENANT_ID_ = @tenantId where ID_ in (
SELECT tempTask.tempId FROM ( SELECT J.ID_ as tempId FROM ACT_RU_JOB J inner join ACT_RE_PROCDEF P on J.PROC_DEF_ID_ = P.ID_ inner join ACT_RE_DEPLOYMENT D on P.DEPLOYMENT_ID_ = D.ID_ where D.ID_ = @deploymentId
) AS tempTask
)

SmartSqlMap.Select:
${limitBefore}
select
RES.* ${limitBetween}
from ACT_RU_JOB RES
where (RES.LOCK_OWNER_ = @parameter)
order by ID_ 
${limitAfter}

${limitBefore}
select RES.* ${limitBetween}
from ACT_RU_JOB RES
where (RETRIES_ > 0)
and (DUEDATE_ is null or DUEDATE_ <= @duedate)
and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ <= @lockExpTime)
and (EXCLUSIVE_ = TRUE)
and (PROCESS_INSTANCE_ID_ = @pid)
${limitAfter}

${limitBefore}
select RES.* ${limitBetween}
from ACT_RU_JOB RES
where (RETRIES_ > 0)
and (DUEDATE_ is null or DUEDATE_ <= @duedate)
and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ <= @lockExpTime)
and (EXCLUSIVE_ = 1)
and (PROCESS_INSTANCE_ID_ = @parameter.pid)
${limitAfter}

SmartSqlMap.select:
	  	${limitBefore}
      	select
      	RES.* ${limitBetween} 
      	from ACT_RU_JOB RES
    	where RES.LOCK_EXP_TIME_ is not null and RES.LOCK_EXP_TIME_ < @lockExpTime
    	${limitAfter}

SmartSqlMap.Insert:
insert into ACT_RU_JOB (
ID_,
REV_,
TYPE_,
LOCK_OWNER_,
LOCK_EXP_TIME_,
EXCLUSIVE_,
EXECUTION_ID_,
PROCESS_INSTANCE_ID_,
PROC_DEF_ID_,
RETRIES_,
EXCEPTION_STACK_ID_,
EXCEPTION_MSG_,
DUEDATE_,
REPEAT_,
HANDLER_TYPE_,
HANDLER_CFG_,
TENANT_ID_)
values (@id,
1,
'timer',
@lockOwner,
@lockExpirationTime,
@exclusive,
@executionId,
@processInstanceId,
@processDefinitionId,
@retries,
@exceptionByteArrayRef,
@exceptionMessage,
@duedate,
@repeat,
@jobHandlerType,
@jobHandlerConfiguration,
@tenantId
)

