SmartSqlMap.Statement:
from ACT_RU_EVENT_SUBSCR RES


    RES.ID_ = #{eventSubscriptionId}


    and RES.PROC_INST_ID_ = #{processInstanceId}


    and RES.EXECUTION_ID_ = #{executionId}


    and RES.ACTIVITY_ID_ = #{activityId}


    and RES.EVENT_TYPE_ = #{eventType}


    and RES.EVENT_NAME_ = #{eventName}


    and RES.TENANT_ID_ = #{tenantId}


    and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)

SmartSqlMap.Select:
select *
from ACT_RU_EVENT_SUBSCR EVT
where (EVENT_TYPE_ = 'message')
and EVENT_NAME_ = #{eventName}
and PROC_INST_ID_ = #{processInstanceId}

select * from ACT_RU_EVENT_SUBSCR EVT left outer join ACT_RU_EXECUTION EXC on EVT.EXECUTION_ID_ = EXC.ID_ where EVENT_TYPE_ = 'signal' and EVENT_NAME_ = #{eventName} and ( (EVT.EXECUTION_ID_ is null) or (EVT.EXECUTION_ID_ is not null AND EXC.SUSPENSION_STATE_ = 1) )
and EVT.TENANT_ID_ = #{tenantId}
and (EVT.TENANT_ID_ = '' or EVT.TENANT_ID_ is null)

select *
from ACT_RU_EVENT_SUBSCR EVT
where (EVENT_TYPE_ = 'signal')
and EVENT_NAME_ = #{eventName}
and PROC_INST_ID_ = #{processInstanceId}

select * from ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = #{eventType}) and ( (CONFIGURATION_ = #{configuration}) or (PROC_DEF_ID_ = #{configuration}) )
and TENANT_ID_ = #{tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

select * from ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = #{eventType}) and (EVENT_NAME_ = #{eventName})
and TENANT_ID_ = #{tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

select * from ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = 'message') and (EVENT_NAME_ = #{eventName}) and EXECUTION_ID_ is null
and TENANT_ID_ = #{tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

SmartSqlMap.Update:
update ACT_RU_EVENT_SUBSCR
REV_ = #{revisionNext}, EVENT_NAME_ = #{eventName}, EXECUTION_ID_ = #{executionId}, PROC_INST_ID_ = #{processInstanceId}, ACTIVITY_ID_ = #{activityId}, CONFIGURATION_ = #{configuration}, CREATED_ = #{created}, PROC_DEF_ID_ = #{processDefinitionId}, TENANT_ID_ = #{tenantId}
where ID_= #{id} and REV_ = #{revision}

update ACT_RU_EVENT_SUBSCR
REV_ = #{revisionNext}, EVENT_NAME_ = #{eventName}, EXECUTION_ID_ = #{executionId}, PROC_INST_ID_ = #{processInstanceId}, ACTIVITY_ID_ = #{activityId}, CONFIGURATION_ = #{configuration}, CREATED_ = #{created}, PROC_DEF_ID_ = #{processDefinitionId}, TENANT_ID_ = #{tenantId}
where ID_= #{id} and REV_ = #{revision}

update ACT_RU_EVENT_SUBSCR
REV_ = #{revisionNext}, EVENT_NAME_ = #{eventName}, EXECUTION_ID_ = #{executionId}, PROC_INST_ID_ = #{processInstanceId}, ACTIVITY_ID_ = #{activityId}, CONFIGURATION_ = #{configuration}, CREATED_ = #{created}, PROC_DEF_ID_ = #{processDefinitionId}, TENANT_ID_ = #{tenantId}
where ID_= #{id} and REV_ = #{revision}
